import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Login() {
  const [loginData, setLoginData] = useState({
    username: "",
    password: "",
    role: "student", // default role
  });
  const [errors, setErrors] = useState({});
  const [submitStatus, setSubmitStatus] = useState("");
  const [isSubmitting, setIsSubmitting] = useState(false);
  const navigate = useNavigate();

  const validate = () => {
    const newErrors = {};
    if (!loginData.username) newErrors.username = "Username is required";
    if (!loginData.password) newErrors.password = "Password is required";
    if (!loginData.role) newErrors.role = "Role is required";
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value });
    setErrors({ ...errors, [e.target.name]: "" });
    setSubmitStatus("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;

    setIsSubmitting(true);
    setSubmitStatus("");
    try {
      // Example API endpoint, adjust as needed
      const response = await axios.post(
        `https://management-q91z.onrender.com/auth/login`,
        {
          username: loginData.username,
          password: loginData.password,
          role: loginData.role,
        }
      );

      if (response.data.success) {
        setSubmitStatus("Login successful!");
        // Save token or user info as needed, then navigate
        if (loginData.role === "student") {
          navigate("/students");
        } else if (loginData.role === "admin") {
          navigate("/admin");
        }
      } else {
        setSubmitStatus("Invalid credentials");
      }
    } catch (error) {
      setSubmitStatus("Error during login");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit} noValidate>
        <div>
          <label>Username: </label>
          <input
            name="username"
            value={loginData.username}
            onChange={handleChange}
          />
          {errors.username && (
            <div style={{ color: "red" }}>{errors.username}</div>
          )}
        </div>
        <div>
          <label>Password: </label>
          <input
            name="password"
            type="password"
            value={loginData.password}
            onChange={handleChange}
          />
          {errors.password && (
            <div style={{ color: "red" }}>{errors.password}</div>
          )}
        </div>
        <div>
          <label>Role: </label>
          <select name="role" value={loginData.role} onChange={handleChange}>
            <option value="student">Student</option>
            <option value="admin">Admin</option>
          </select>
          {errors.role && <div style={{ color: "red" }}>{errors.role}</div>}
        </div>
        <button type="submit" disabled={isSubmitting}>
          {isSubmitting ? "Logging in..." : "Login"}
        </button>
      </form>
      {submitStatus && <p>{submitStatus}</p>}
    </div>
  );
}

export default Login;
